export default function ContentPage() {
  const { user } = useAuth();
  const navigate = useNavigate();

  const [items, setItems] = useState<Content[]>([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState<"all" | "Film" | "Serija">("all");
  const [sort, setSort] = useState<"title-asc" | "title-desc" | "rating-asc" | "rating-desc">("title-asc");
  const [modalOpen, setModalOpen] = useState(false);
  const [modalContent, setModalContent] = useState<Content | null>(null);
  const [rating, setRating] = useState(1);

  useEffect(() => {
    setTimeout(() => {
      setItems(mockContent);
      setLoading(false);
    }, 300);
  }, []);

  const handleLogout = () => navigate("/register");

  const filteredItems = items
    .filter((item) => filter === "all" || item.type === filter)
    .sort((a, b) => {
      if (sort.startsWith("title")) {
        return sort === "title-asc" ? a.title.localeCompare(b.title) : b.title.localeCompare(a.title);
      } else {
        return sort === "rating-asc" ? a.prosecnaOcena - b.prosecnaOcena : b.prosecnaOcena - a.prosecnaOcena;
      }
    });

  const openModal = (item?: Content) => {
    setModalContent(item || null);
    setModalOpen(true);
    if (item) setRating(item.prosecnaOcena);
  };
  const closeModal = () => setModalOpen(false);

  const saveContent = (content: Content) => {
    if (user?.uloga === "user" && modalContent) {
      // Ocena korisnika
      const updated = items.map((i) =>
        i.id === modalContent.id ? { ...i, prosecnaOcena: content.prosecnaOcena } : i
      );
      setItems(updated);
    } else {
      // Admin dodaje novi sadržaj
      if (content.id) {
        setItems(items.map((i) => (i.id === content.id ? content : i)));
      } else {
        const nextId = Math.max(0, ...items.map((i) => i.id || 0)) + 1;
        setItems([...items, { ...content, id: nextId }]);
      }
    }
    closeModal();
  };

  if (loading) return <div className="p-10 text-center text-xl font-medium">Učitavanje...</div>;

  return (
    <div className="min-h-screen bg-gradient-to-tr from-purple-100 via-pink-100 to-orange-100 p-6">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-4xl font-extrabold text-purple-800">🎬 KATALOG</h1>
        <div className="flex items-center space-x-4">
          <span className="text-lg font-medium text-purple-700">Uloga: {user?.uloga}</span>
          <button
            onClick={handleLogout}
            className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded-xl"
          >
            Odjavi se
          </button>
        </div>
      </div>

      <div className="flex space-x-4 mb-6">
        <select
          value={filter}
          onChange={(e) => setFilter(e.target.value as "all" | "Film" | "Serija")}
          className="px-4 py-2 rounded-xl border border-gray-300 bg-purple-50"
        >
          <option value="all">Sve kategorije</option>
          <option value="Film">Film</option>
          <option value="Serija">Serija</option>
        </select>
        <select
          value={sort}
          onChange={(e) => setSort(e.target.value as typeof sort)}
          className="px-4 py-2 rounded-xl border border-gray-300 bg-purple-50"
        >
          <option value="title-asc">Naziv A-Z</option>
          <option value="title-desc">Naziv Z-A</option>
          <option value="rating-asc">Ocena rastuće</option>
          <option value="rating-desc">Ocena opadajuće</option>
        </select>

        {user?.uloga === "admin" && (
          <button
            onClick={() => openModal()}
            className="bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded-xl"
          >
            Dodaj novi sadržaj
          </button>
        )}
      </div>

      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-8">
        {filteredItems.map((item) => (
          <div key={item.id} className="bg-white rounded-2xl shadow-xl overflow-hidden">
            <img src={item.imageURL} alt={item.title} className="w-full h-64 object-cover" />
            <div className="p-4">
              <h2 className="text-xl font-bold text-purple-800">{item.title}</h2>
              <p className="text-gray-500">{item.genre} | {item.type}</p>
              <p className="text-yellow-600 font-semibold mb-2">Ocena: {item.prosecnaOcena.toFixed(1)}</p>

              {item.episodes && item.episodes.length > 0 && (
                <div className="mb-2">
                  <h3 className="font-semibold text-gray-700">Epizode:</h3>
                  <ul className="list-disc pl-5 text-gray-600 text-sm max-h-32 overflow-y-auto">
                    {item.episodes.map((ep, idx) => (
                      <li key={idx}>{`Sezona ${ep.season}, Epizoda ${ep.episodeNumber}: ${ep.title}`}</li>
                    ))}
                  </ul>
                </div>
              )}

              {user?.uloga === "user" && (
                <button
                  onClick={() => openModal(item)}
                  className="w-full bg-blue-500 hover:bg-blue-600 text-white py-2 rounded-xl"
                >
                  Ocenite
                </button>
              )}
            </div>
          </div>
        ))}
      </div>

      {modalOpen && (
        <AddModal
          modalContent={modalContent}
          onClose={closeModal}
          onSave={saveContent}
          userRole={user?.uloga || "user"}
        />
      )}
    </div>
  );
}